import {Datepicker, Dropdown, FileUpload, Input, Modal, Table, TableHead, Typography} from 'client-library';
import React, {useEffect, useState} from 'react';
import {Controller, useFieldArray, useForm} from 'react-hook-form';
import FileList from '../../components/fileList/fileList';
import {pdvOptions} from '../../constants';
import useAppContext from '../../context/useAppContext';
import {FileUploadWrapper} from '../../screens/formOrder/styles';
import useOrderListReceive from '../../services/graphql/orders/hooks/useOrderListReceive';
import {FileItem, FileResponseItem} from '../../types/fileUploadType';
import {OrderArticleType} from '../../types/graphql/articleTypes';
import {ReceiveItemStatus} from '../../types/graphql/orderListTypes';
import {parseDate, parseDateForBackend} from '../../utils/dateUtils';
import {convertToCurrency} from '../../utils/stringUtils';
import {DatepickersWrapper, FormWrapper, HeaderSection, StyledInput, TextareaWrapper, Title} from './styles';

type ReceiveItemForm = {
  invoice_date: string;
  date_system: string;
  invoice_number: string;
  delivery_number: string;
  delivery_date: string;
  description: string;
  receive_file: FileItem[] | null;
  delivery_file: FileItem[] | null;
  articles?: OrderArticleType[];
};

const initialValues: ReceiveItemForm = {
  invoice_date: '',
  date_system: '',
  invoice_number: '',
  description: '',
  receive_file: null,
  articles: [],
  delivery_number: '',
  delivery_date: '',
  delivery_file: null,
};

interface ReceiveItemsModalProps {
  data?: any;
  open: boolean;
  onClose: () => void;
  alert?: any;
  fetch: () => void;
}

export const ReceiveItemsModal: React.FC<ReceiveItemsModalProps> = ({data, open, onClose, alert, fetch}) => {
  const [uploadedFilesForReceive, setUploadedFilesForReceive] = useState<FileList | null>(null);
  const [uploadedFilesForDelivery, setUploadedFilesForDelivery] = useState<FileList | null>(null);
  const [receiveFileIds, setReceiveFileIds] = useState<number>();
  const [deliveryFileIds, setDeliveryFileIds] = useState<number>();

  const {mutate: orderListReceive, loading: isSaving} = useOrderListReceive();
  const [filesToDelete, setFilesToDelete] = useState<number[]>();
  const isException = data[0]?.public_procurement?.id === 0;
  const {
    register,
    handleSubmit,
    control,
    formState: {errors},
    reset,
    clearErrors,
    watch,
    setValue,
  } = useForm({defaultValues: initialValues});

  const {fields} = useFieldArray({
    control,
    name: 'articles',
    keyName: 'key',
  });

  const receiveFile = watch('receive_file');
  const deliveryFile = watch('delivery_file');

  const {
    fileService: {uploadFile, batchDeleteFiles},
  } = useAppContext();

  const tableHeads: TableHead[] = [
    {
      title: 'Naziv',
      accessor: 'title',
      type: 'text',
    },
    {
      title: 'Jedinica mjere:',
      accessor: 'unit',
      type: 'custom',
      renderContents: (unit: number) => {
        return <Typography variant="bodyMedium" content={unit || 'Kom'} />;
      },
      shouldRender: !isException,
    },

    {title: 'Poručeno', accessor: 'amount', type: 'text'},
    {
      title: 'Jedinična cijena',
      accessor: 'net_price',
      type: 'custom',
      renderContents: (net_price: number, _, index) => {
        return data[0].status !== ReceiveItemStatus.RECEIVED && !data[0]?.is_pro_forma_invoice ? (
          <Input
            leftContent={<Typography variant="bodyMedium" content="€" />}
            {...register(`articles.${index}.net_price`, {
              required: 'Ovo polje je obavezno',
            })}
          />
        ) : (
          <Typography
            variant="bodyMedium"
            content={
              `${net_price.toLocaleString('sr-RS', {
                minimumFractionDigits: 2,
                maximumFractionDigits: 2,
              })} €` || ''
            }
          />
        );
      },
      shouldRender: isException,
    },
    {
      title: 'PDV',
      accessor: 'vat_percentage',
      type: 'custom',
      renderContents: (vat_percentage: any, _, index) => {
        return data[0].status !== ReceiveItemStatus.RECEIVED && !data[0]?.is_pro_forma_invoice ? (
          <Controller
            name={`articles.${index}.vat_percentage`}
            control={control}
            rules={{required: 'Ovo polje je obavezno'}}
            render={({field: {onChange, name, value}}) => {
              return (
                <Dropdown
                  onChange={onChange}
                  value={value as any}
                  name={name}
                  options={pdvOptions}
                  error={errors.articles?.[index]?.vat_percentage?.message}
                />
              );
            }}
          />
        ) : (
          <Typography variant="bodyMedium" content={`${vat_percentage?.id} %` || ''} />
        );
      },
      shouldRender: isException,
    },
    {
      title: 'Ukupna vrijednost',
      accessor: 'total',
      type: 'custom',
      renderContents: (_, row, index) => {
        const total = calculateTotalPrice(row, index);
        return <Typography variant="bodyMedium" content={total ? convertToCurrency(total) : ''} />;
      },
      shouldRender: isException,
    },
    {
      title: 'Ukupna vrijednost(sa PDV-om)',
      accessor: 'total_price',
      type: 'custom',
      renderContents: (total_price: number) => {
        return <Typography variant="bodyMedium" content={total_price ? convertToCurrency(totalPrice) : ''} />;
      },
      shouldRender: !isException,
    },
  ];

  const calculateTotalPrice = (item: OrderArticleType, index: number) => {
    const net_price = watch('articles')?.[index]?.net_price;
    const vat_percentage = watch('articles')?.[index]?.vat_percentage as any;
    const totalArticlePrice = net_price && vat_percentage?.id ? net_price * (1 + vat_percentage?.id / 100) : net_price;
    return totalArticlePrice && totalArticlePrice * item.amount;
  };
  const handleReceiveFileUpload = async (files: FileList) => {
    setUploadedFilesForReceive(files);

    const formData = new FormData();
    Array.from(files).forEach((file: File) => {
      formData.append('file', file);
    });

    const response = await uploadFile(formData);
    setReceiveFileIds(response[0]?.id);
  };

  const handleDeliveryFileUpload = async (files: FileList) => {
    setUploadedFilesForDelivery(files);
    const formData = new FormData();
    Array.from(files).forEach((file: File) => {
      formData.append('file', file);
    });

    const response = await uploadFile(formData);
    setDeliveryFileIds(response[0]?.id);
  };

  const onDeleteFile = (id: number) => {
    const filteredFiles = receiveFile?.filter((item: FileItem) => item.id !== id) || null;
    setValue('receive_file', filteredFiles);
    setFilesToDelete(prevState => [...(prevState || []), id]);
  };

  const totalPrice = [data[0]]?.reduce((sum: any, article: any) => {
    const price = parseFloat(article?.total_bruto);

    return sum + price;
  }, 0);

  const totalNeto = [data[0]]?.reduce((sum: any, article: any) => {
    const price = parseFloat(article?.total_neto?.toFixed(2));

    return sum + price;
  }, 0);

  const onSubmit = async (values: any) => {
    if (isSaving) return;

    if (filesToDelete && filesToDelete.length > 0) {
      await batchDeleteFiles(filesToDelete);
    }

    const payload = {
      order_id: data[0]?.id,
      invoice_date: parseDateForBackend(values?.invoice_date),
      invoice_number: values?.invoice_number,
      description: values?.description,
      date_system: parseDateForBackend(values?.date_system),
      receive_file: data[0]?.file?.id ? data[0]?.file?.id : receiveFileIds ? receiveFileIds : null,
      delivery_file_id: data[0]?.delivery_file?.id
        ? data[0]?.delivery_file?.id
        : deliveryFileIds
        ? deliveryFileIds
        : null,
      delivery_number: values?.delivery_number,
      delivery_date: parseDateForBackend(values?.delivery_date),
      articles: !data[0]?.id
        ? isException && values?.articles && values?.articles?.length > 0
          ? values.articles?.map((item: any) => {
              const matchedItem = data[0]?.articles.find((article: {id: any}) => article.id === item.id);
              return {
                id: item.id,
                net_price:
                  data[0].status !== ReceiveItemStatus.RECEIVED
                    ? typeof item?.net_price === 'string'
                      ? parseFloat(item.net_price.replace(',', '.'))
                      : item.net_price
                    : data[0]?.is_pro_forma_invoice && matchedItem
                    ? matchedItem.vat_percentage
                    : item.net_price,
                vat_percentage:
                  data[0]?.is_pro_forma_invoice && matchedItem ? matchedItem.vat_percentage : item?.vat_percentage?.id,
              };
            })
          : []
        : undefined,
    };

    orderListReceive(
      payload,
      () => {
        fetch();
        alert.success('Uspješno sačuvano.');
        onClose();
      },
      () => {
        alert.error('Greška. Promjene nisu sačuvane.');
      },
    );
  };

  useEffect(() => {
    if (data[0]) {
      reset({
        ...data[0],
        invoice_date: data[0]?.invoice_date || '',
        date_system: data[0]?.date_system || '',
        invoice_number: data[0]?.invoice_number || '',
        description: data[0]?.description || '',
        receive_file: data[0]?.receive_file || null,
        delivery_file: data[0]?.delivery_file,
        articles: data[0]?.articles
          .filter((item: OrderArticleType) => item.amount !== 0)
          .map((item: OrderArticleType) => {
            return {
              ...item,
              net_price: item.net_price || '',
              vat_percentage: {id: item.vat_percentage, title: `${item.vat_percentage} %`},
            };
          }),
      });
    }
  }, [data]);

  return (
    <Modal
      open={open}
      onClose={() => {
        onClose();
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(onSubmit)}
      buttonLoading={isSaving}
      width={870}
      content={
        <FormWrapper>
          <div>
            {Number(data[0]?.public_procurement?.id) !== 0 && (
              <Title
                variant="bodySmall"
                style={{fontWeight: 600}}
                content={`JAVNA NABAVKA: JN-${data[0]?.public_procurement?.title || ''}`}
              />
            )}
            {Number(data[0]?.group_of_articles?.id) !== 0 && (
              <Title
                variant="bodySmall"
                style={{fontWeight: 600}}
                content={`GRUPA ARTIKALA: ${data[0]?.group_of_articles?.title || ''}`}
              />
            )}
            <Title
              style={{fontWeight: 600}}
              variant="bodySmall"
              content={`DOBAVLJAČ: ${data[0]?.supplier?.title || ''} `}
            />
          </div>
          <HeaderSection>
            <div>
              <StyledInput {...register('delivery_number')} label="BROJ OTPREMNICE:" />
              <DatepickersWrapper>
                <Controller
                  name="delivery_date"
                  control={control}
                  render={({field: {onChange, name, value}}) => (
                    <Datepicker
                      onChange={onChange as any}
                      label="DATUM OTPREMNICE:"
                      name={name}
                      value={value ? parseDate(value) : ''}
                      error={errors.delivery_date?.message as string}
                    />
                  )}
                />
                <Controller
                  name="date_system"
                  control={control}
                  rules={{
                    validate: {
                      customDateValidation: value => {
                        const dateSystem = new Date(value);
                        dateSystem.setHours(0, 0, 0, 0);

                        const orderDate = new Date(data[0].date_order);
                        orderDate.setHours(0, 0, 0, 0);

                        if (dateSystem < orderDate) {
                          return 'Datum prijema robe ne može biti stariji od datuma narudžbenice';
                        }
                        return true;
                      },
                    },
                    required: 'Ovo polje je obavezno.',
                  }}
                  render={({field: {onChange, name, value}}) => (
                    <Datepicker
                      onChange={onChange as any}
                      label="DATUM PRIJEMA ROBE:"
                      name={name}
                      value={value ? parseDate(value) : ''}
                      error={errors.date_system?.message as string}
                      isRequired
                    />
                  )}
                />
              </DatepickersWrapper>
              <FileUploadWrapper>
                <FileUpload
                  icon={null}
                  files={uploadedFilesForDelivery}
                  variant="secondary"
                  onUpload={handleDeliveryFileUpload}
                  note={<Typography variant="bodySmall" content="Otpremnica" />}
                  hint="Fajlovi neće biti učitani dok ne sačuvate prijemnicu."
                  buttonText="Učitaj"
                />
              </FileUploadWrapper>
              {deliveryFile && deliveryFile.length !== 0 && <FileList files={[deliveryFile] as any} />}
            </div>
            <div>
              <StyledInput {...register('invoice_number')} label="BROJ FAKTURE:" />
              <DatepickersWrapper>
                <Controller
                  name="invoice_date"
                  control={control}
                  rules={{
                    validate: {
                      customDateValidation: value => {
                        const invoiceDate = new Date(value);
                        invoiceDate.setHours(0, 0, 0, 0);

                        const orderDate = new Date(data[0].date_order);
                        orderDate.setHours(0, 0, 0, 0);

                        if (invoiceDate < orderDate) {
                          return 'Datum fakture ne može biti stariji od datuma narudžbenice';
                        }
                        return true;
                      },
                    },
                  }}
                  render={({field: {onChange, name, value}}) => (
                    <div style={{width: '100%'}}>
                      <Datepicker
                        onChange={onChange as any}
                        label="DATUM FAKTURE:"
                        name={name}
                        value={value ? parseDate(value) : ''}
                        error={errors.invoice_date?.message as string}
                      />
                    </div>
                  )}
                />
              </DatepickersWrapper>
              <FileUploadWrapper>
                <FileUpload
                  icon={null}
                  files={uploadedFilesForReceive}
                  variant="secondary"
                  onUpload={handleReceiveFileUpload}
                  note={<Typography variant="bodySmall" content="Račun" />}
                  hint="Fajlovi neće biti učitani dok ne sačuvate prijemnicu."
                  buttonText="Učitaj"
                />
              </FileUploadWrapper>
              {receiveFile && receiveFile.length !== 0 && (
                <FileList files={receiveFile} onDelete={onDeleteFile} isInModal={true} />
              )}
            </div>
          </HeaderSection>
          <TextareaWrapper>
            <Input {...register('description')} label="NAPOMENA:" textarea={true} />
            {Number(data[0]?.public_procurement?.id) !== 0 && (
              <>
                <Input
                  label="UKUPNA VRIJEDNOST NARUDŽBENICE (BEZ PDV-a):"
                  value={totalNeto.toLocaleString('sr-RS', {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}
                  disabled={true}
                  style={{marginBlock: 5}}
                />
                <Input
                  label="UKUPNA VRIJEDNOST NARUDŽBENICE (SA PDV-om):"
                  value={totalPrice.toLocaleString('sr-RS', {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })}
                  disabled={true}
                />
              </>
            )}
          </TextareaWrapper>

          <Table tableHeads={tableHeads} data={fields} />
        </FormWrapper>
      }
      title={'KREIRAJ NOVU PRIJEMNICU'}
    />
  );
};

import {EditIconTwo, Table, TableHead, Theme, Typography} from 'client-library';
import React, {useMemo, useState} from 'react';
import useAppContext from '../../context/useAppContext';
import {SectionBox} from '../../shared/styles';
import {parseDate} from '../../utils/dateUtils';
import {ReceiveItemsModal} from './receiveItemsModal';

interface ReceiveItemsTableProps {
  data: any;
  fetch: () => any;
  loading: boolean;
  isDisabled: boolean;
}

export const ReceiveItemsTable: React.FC<ReceiveItemsTableProps> = ({data, fetch, loading, isDisabled}) => {
  const {alert} = useAppContext();
  const [showModal, setShowModal] = useState(false);
  const [selectedItemId, setSelectedItemId] = useState(0);

  const tableHeads: TableHead[] = [
    {
      title: 'Datum prijema robe',
      accessor: 'date_system',
      type: 'custom',
      renderContents: (date_system: string) => {
        return <Typography variant="bodySmall" content={date_system ? parseDate(date_system) : ''} />;
      },
    },
    {
      title: 'Broj fakture',
      accessor: 'invoice_number',
      type: 'text',
    },
    {
      title: 'Datum fakture',
      accessor: 'invoice_date',
      type: 'custom',
      renderContents: (invoice_date: string) => {
        return <Typography variant="bodySmall" content={invoice_date ? parseDate(invoice_date) : ''} />;
      },
    },
    {
      title: 'Ukupna vrijednost (sa PDV-OM):',
      accessor: 'total_bruto',
      type: 'custom',
      renderContents: (total_bruto: number) => {
        return (
          <Typography
            variant="bodyMedium"
            content={
              total_bruto
                ? total_bruto.toLocaleString('sr-RS', {
                    minimumFractionDigits: 2,
                    maximumFractionDigits: 2,
                  })
                : ''
            }
          />
        );
      },
      shouldRender: Number(data?.public_procurement?.id) !== 0,
    },
    {title: '', accessor: 'TABLE_ACTIONS', type: 'tableActions'},
  ];

  const selectedItem = useMemo(() => {
    return [data]?.find((item: any) => item.id === selectedItemId);
  }, [data?.articles, selectedItemId]);

  const handleEdit = (id: number) => {
    setSelectedItemId(id);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <SectionBox>
      <Table
        tableHeads={tableHeads}
        data={[data] || []}
        isLoading={loading}
        tableActions={[
          {
            name: 'Izmijeni',
            onClick: item => handleEdit(item.id),
            icon: <EditIconTwo stroke={Theme?.palette?.gray800} />,
            shouldRender: () => !isDisabled,
          },
        ]}
      />
      {showModal && (
        <ReceiveItemsModal open={showModal} onClose={closeModal} fetch={fetch} data={[selectedItem]} alert={alert} />
      )}
    </SectionBox>
  );
};

import {TableHead, Typography} from 'client-library';
import {parseDate} from '../../utils/dateUtils';

export const tableHeads: TableHead[] = [
  {
    title: 'Datum narudžbenice',
    accessor: 'date_order',
    sortable: true,
    type: 'custom',
    renderContents: (date_order: string) => {
      return <Typography variant="bodyMedium" content={date_order ? parseDate(date_order) : ''} />;
    },
  },
  {
    title: 'Ukupna cijena',
    sortable: true,
    accessor: 'total_bruto',
    type: 'custom',
    renderContents: (total_bruto: number) => {
      return (
        <Typography
          variant="bodyMedium"
          content={
            total_bruto
              ? total_bruto?.toLocaleString('sr-RS', {
                  minimumFractionDigits: 2,
                  maximumFractionDigits: 2,
                })
              : 0.0
          }
        />
      );
    },
  },
  {
    title: 'Nabavka',
    accessor: 'public_procurement',
    type: 'custom',
    renderContents: (public_procurement, row) => {
      return (
        <Typography
          variant="bodyMedium"
          content={
            row.is_pro_forma_invoice && row?.invoice_number !== ''
              ? 'Izuzeće - račun'
              : row.is_pro_forma_invoice
              ? 'Izuzeće - predračun'
              : public_procurement.title
              ? public_procurement.title
              : 'Izuzeće'
          }
        />
      );
    },
  },
  {
    title: 'Dobavljač',
    accessor: 'supplier',
    type: 'custom',
    renderContents: (supplier: any) => {
      return <Typography variant="bodyMedium" content={supplier.title} />;
    },
  },
  {title: '', accessor: 'TABLE_ACTIONS', type: 'tableActions'},
];

const getCurrentYear = () => {
  return new Date().getFullYear();
};

const getYearList = () => {
  const currentYear = getCurrentYear();
  const years = [];
  for (let i = currentYear - 5; i <= currentYear + 5; i++) {
    years.push(i.toString());
  }
  return years;
};

export const YearList = getYearList()
  .map(year => ({id: year, title: year}))
  .reverse();

export const yearsForDropdown = () => {
  const maxOffset = 10;
  const thisYear = new Date().getFullYear();
  const allYears = [];
  allYears.push({id: 0, title: 'Sve'});
  for (let x = 0; x < maxOffset; x++) {
    allYears.push({id: Number(thisYear - x), title: (thisYear - x).toString()});
  }
  return allYears;
};

import {Accordion, Button, ChevronDownIcon, Table, TableHead, Typography, FileUpload} from 'client-library';
import React, {useMemo, useState} from 'react';
import FileList from '../../components/fileList/fileList';
import {ReceiveItemsModal} from '../../components/receiveItems/receiveItemsModal';
import {ReceiveItemsTable} from '../../components/receiveItems/receiveItemsTable';
import useAppContext from '../../context/useAppContext';
import useDeleteOrderListReceive from '../../services/graphql/orders/hooks/useDeleteOrderListReceive';
import useGetOrderList from '../../services/graphql/orders/hooks/useGetOrderList';
import {NotificationsModal} from '../../shared/notifications/notificationsModal';
import ScreenWrapper from '../../shared/screenWrapper';
import {CustomDivider, MainTitle, Row, SectionBox} from '../../shared/styles';
import {OrderArticleType} from '../../types/graphql/articleTypes';
import {parseDate} from '../../utils/dateUtils';
import {
  AccordionHeader,
  AccordionIconsWrapper,
  AccordionWrapper,
  ButtonContainer,
  FileUploadWrapper,
  FormControls,
  FormFooter,
  OrderInfo,
} from './styles';
import {FileResponseItem} from '../../types/fileUploadType';
import useOrderListInsert from '../../services/graphql/orders/hooks/useInsertOrderList';
import {useForm} from 'react-hook-form';
import {convertToCurrency} from '../../utils/stringUtils';
import usePassOrderToFinance from '../../services/graphql/passOrderListToFinance/usePassOrderToFinance';

export const FormOrderDetailsPreview: React.FC = () => {
  const {
    alert,
    breadcrumbs,
    navigation: {location, navigate},
    reportService: {generatePdf},
    fileService: {uploadFile},
  } = useAppContext();

  const {handleSubmit} = useForm();

  const [showModal, setShowModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [isOpen, setIsOpen] = useState<number>(0);
  const [uploadedFile, setUploadedFile] = useState<FileList | null>(null);

  let date = '';
  const url = location.pathname;
  const orderId = Number(url?.split('/').at(-2));

  const [form, setForm] = useState({
    page: 1,
    size: 10,
    id: orderId,
    status: '',
    search: '',
  });

  const {orders, loading, fetch} = useGetOrderList(form);
  const {mutate: deleteOrderListReceive} = useDeleteOrderListReceive();
  const {mutate: orderListInsert} = useOrderListInsert();
  const {passOrderToFinance} = usePassOrderToFinance();

  const supplier = orders[0]?.supplier;
  const dateOrder = orders[0]?.date_order || '';

  if (dateOrder) {
    const convertDate = new Date(dateOrder);
    date = parseDate(convertDate);
  }

  const tableHeads: TableHead[] = [
    {
      title: 'Naziv',
      accessor: 'title',
      type: 'text',
    },
    {
      title: 'Bitne karakteristike',
      accessor: 'description',
      type: 'text',
    },
    {
      title: 'Jedinica mjere',
      accessor: 'unit',
      type: 'text',
      shouldRender: Number(orders[0]?.public_procurement?.id) !== 0,
    },
    {
      title: 'Količina',
      accessor: 'amount',
      type: 'custom',
      renderContents: (amount: number) => {
        return <Typography variant="bodyMedium" content={amount ? parseFloat(amount?.toFixed(2)) : 0} />;
      },
    },
    {
      title: 'Jedinična cijena',
      accessor: 'net_price',
      type: 'custom',
      renderContents: (net_price: number) => {
        return <Typography variant="bodyMedium" content={convertToCurrency(net_price)} />;
      },
      shouldRender: orders[0]?.is_pro_forma_invoice,
    },
    {
      title: 'PDV',
      accessor: 'vat_percentage',
      type: 'custom',
      renderContents: (vat_percentage: string) => {
        return <Typography variant="bodyMedium" content={`${vat_percentage} %` || ''} />;
      },
      shouldRender: orders[0]?.is_pro_forma_invoice,
    },
    {
      title: 'Ukupna vrijednost (bez PDV-a)',
      accessor: 'total',
      type: 'custom',
      renderContents: (_, row, index) => {
        const total = calculateTotalPrice(row, index);
        return <Typography variant="bodyMedium" content={total ? convertToCurrency(total) : ''} />;
      },
      shouldRender: orders[0]?.is_pro_forma_invoice,
    },
    {
      title: 'Ukupna vrijednost (sa PDV-om)',
      accessor: 'total_price',
      type: 'custom',
      renderContents: (_, row, index) => {
        const total = calculateTotalPriceWithVat(row, index);
        return <Typography variant="bodyMedium" content={total ? convertToCurrency(total) : ''} />;
      },
      shouldRender: orders[0]?.is_pro_forma_invoice,
    },
  ];

  const calculateTotalPrice = (item: OrderArticleType, index: number) => {
    const net_price = item.net_price;
    return net_price * item.amount;
  };

  const calculateTotalPriceWithVat = (item: OrderArticleType, index: number) => {
    const net_price = item.net_price;
    const vat_percentage = item.vat_percentage;
    const totalArticlePrice = net_price * (1 + vat_percentage / 100);
    const totalPrice = totalArticlePrice * item.amount;
    return totalPrice;
  };

  const mappedOrder = useMemo(() => {
    if (orders) {
      return orders[0]?.articles
        ?.filter((article: OrderArticleType) => article.amount !== 0)
        .map(article => {
          return {
            ...article,
            order_id: orderId,
            unit: 'Kom',
          };
        });
    } else {
      return [];
    }
  }, [orders, orderId]);

  const handleAddReceiveItems = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const openAccordion = (id: number) => {
    setIsOpen(prevState => (prevState === id ? 0 : id));
  };

  const handleCloseDeleteModal = () => {
    setShowDeleteModal(false);
  };

  const handleUpload = (files: FileList) => {
    setUploadedFile(files);
  };

  const handleDelete = () => {
    if (showDeleteModal) {
      deleteOrderListReceive(
        orderId,
        () => {
          setShowDeleteModal(false);
          fetch();
          alert.success('Uspješno obrisano');
        },
        () => {
          setShowDeleteModal(false);
          alert.error('Došlo je do greške pri brisanju');
        },
      );
    }
  };

  const printOrder = () => {
    if (orders.length) {
      generatePdf('ACCOUNTING_ORDER', orders[0]);
    }
  };

  const onSubmit = async () => {
    if (uploadedFile) {
      const formData = new FormData();
      formData.append('file', uploadedFile[0]);

      await uploadFile(formData, (files: FileResponseItem[]) => {
        setUploadedFile(null);
        const payload = {
          id: orderId,
          order_file: files[0].id || null,
          date_order: orders[0].date_order,
          public_procurement_id: orders[0].public_procurement?.id,
          supplier_id: orders[0]?.supplier?.id,
          group_of_articles_id: orders[0]?.group_of_articles?.id,
          is_pro_forma_invoice: orders[0]?.is_pro_forma_invoice,
          pro_forma_invoice_number: orders[0]?.pro_forma_invoice_number,
          pro_forma_invoice_date: orders[0]?.pro_forma_invoice_date,
        };

        orderListInsert(
          payload as any,
          () => {
            fetch();
            alert.success('Uspješno sačuvano.');
          },
          () => {
            alert.error('Greška. Promjene nisu sačuvane.');
          },
        );
      });

      return;
    }
  };

  const orderFile = orders[0]?.order_file;
  const receiveFile = orders[0]?.receive_file;
  const deliveryFile = orders[0]?.delivery_file;

  const sendToFinance = async () => {
    passOrderToFinance(
      orderId,
      () => {
        fetch();
        alert.success('Uspješno proslijedjeno finansijama.');
      },
      () => {
        alert.error('Greška. Nije moguće proslijediti finansijama.');
      },
    );

    return;
  };

  return (
    <ScreenWrapper>
      <SectionBox>
        <MainTitle
          variant="bodyMedium"
          content={
            orders[0]?.is_pro_forma_invoice && orders[0]?.invoice_number !== ''
              ? `RAČUN - BROJ. N${orderId}`
              : orders[0]?.is_pro_forma_invoice
              ? `PREDRAČUN - BROJ. N${orderId}`
              : `NARUDŽBENICA - BROJ. N${orderId}`
          }
        />
        <CustomDivider />
        <OrderInfo>
          <div>
            {Number(orders[0]?.public_procurement?.id) !== 0 && (
              <Row>
                <Typography variant="bodySmall" style={{fontWeight: 600}} content={'JAVNA NABAVKA:'} />
                <Typography variant="bodySmall" content={`${orders && orders[0]?.public_procurement?.title}`} />
              </Row>
            )}
            {Number(orders[0]?.public_procurement?.id) === 0 && orders[0]?.invoice_number === '' && (
              <Row>
                <Typography variant="bodySmall" style={{fontWeight: 600}} content={'GRUPA ARTIKALA:'} />
                <Typography variant="bodySmall" content={`${orders && orders[0]?.group_of_articles?.title}`} />
              </Row>
            )}

            <Row>
              <Typography variant="bodySmall" style={{fontWeight: 600}} content={'DOBAVLJAČ:'} />
              <Typography variant="bodySmall" content={`${supplier?.title || ''} `} />
            </Row>
            <>
              {!orders[0]?.is_pro_forma_invoice && orders[0]?.invoice_number !== '' && (
                <Row>
                  <Typography variant="bodySmall" style={{fontWeight: 600}} content={'DATUM:'} />
                  <Typography variant="bodySmall" content={`${date || ''}`} />
                </Row>
              )}

              <>
                <Row>
                  {orders[0]?.is_pro_forma_invoice && orders[0]?.pro_forma_invoice_number && (
                    <>
                      <Typography
                        variant="bodySmall"
                        style={{fontWeight: 600, marginRight: 30}}
                        content={'BROJ PREDRAČUNA:'}
                      />
                      <Typography variant="bodySmall" content={`${orders[0].pro_forma_invoice_number || ''} `} />
                    </>
                  )}

                  {orders[0]?.invoice_number !== '' && orders[0]?.invoice_number !== null && (
                    <>
                      <Typography variant="bodySmall" style={{fontWeight: 600}} content={'BROJ RAČUNA:'} />
                      <Typography variant="bodySmall" content={`${orders[0]?.invoice_number || ''} `} />
                    </>
                  )}
                </Row>
                <Row>
                  {orders[0]?.is_pro_forma_invoice && orders[0]?.pro_forma_invoice_date && (
                    <>
                      <Typography variant="bodySmall" style={{fontWeight: 600}} content={'DATUM PREDRAČUNA:'} />
                      <Typography
                        variant="bodySmall"
                        content={`${parseDate(orders[0].pro_forma_invoice_date) || ''} `}
                      />
                    </>
                  )}
                  {orders[0]?.invoice_date && (
                    <>
                      <Typography variant="bodySmall" style={{fontWeight: 600}} content={'DATUM RAČUNA:'} />
                      <Typography variant="bodySmall" content={`${parseDate(orders[0]?.invoice_date) || ''} `} />
                    </>
                  )}
                </Row>
              </>
              {!orders[0]?.invoice_date && (
                <Row>
                  <FileUploadWrapper>
                    <FileUpload
                      icon={null}
                      files={uploadedFile}
                      variant="secondary"
                      onUpload={handleUpload}
                      note={
                        <Typography
                          variant="bodySmall"
                          content={
                            orders[0]?.is_pro_forma_invoice && orders[0]?.invoice_number !== ''
                              ? 'Račun'
                              : orders[0]?.is_pro_forma_invoice
                              ? 'Predračun'
                              : 'Narudžbenica'
                          }
                        />
                      }
                      hint={`Fajlovi neće biti učitani dok ne sačuvate ${
                        orders[0]?.is_pro_forma_invoice && orders[0]?.invoice_number !== ''
                          ? 'račun'
                          : orders[0]?.is_pro_forma_invoice
                          ? 'predračun'
                          : 'narudžbenicu.'
                      }`}
                      buttonText="Učitaj"
                    />
                  </FileUploadWrapper>
                </Row>
              )}
              {orderFile?.id !== 0 && (
                <Row>
                  <Typography
                    variant="bodySmall"
                    style={{fontWeight: 600}}
                    content={
                      orders[0]?.is_pro_forma_invoice && orders[0]?.invoice_number !== ''
                        ? 'RAČUN:'
                        : orders[0]?.is_pro_forma_invoice
                        ? 'PREDRAČUN:'
                        : 'NARUDŽBENICA:'
                    }
                  />
                  <FileList files={(orderFile && [orderFile]) ?? []} />
                </Row>
              )}
              {receiveFile && receiveFile.length !== 0 && (
                <Row>
                  <Typography variant="bodySmall" style={{fontWeight: 600}} content={'FAKTURA:'} />
                  <FileList files={(receiveFile && receiveFile) ?? []} />
                </Row>
              )}
              {deliveryFile && deliveryFile.id !== 0 && (
                <Row>
                  <Typography variant="bodySmall" style={{fontWeight: 600}} content={'OTPREMNICA:'} />
                  <FileList files={(deliveryFile && [deliveryFile]) ?? []} />
                </Row>
              )}
            </>
          </div>
          <ButtonContainer>
            {((orders[0]?.invoice_number && !!orders[0]?.receive_file?.length && orders[0]?.invoice_date) ||
              (orders[0]?.is_pro_forma_invoice && orders[0]?.pro_forma_invoice_number)) && (
              <Button
                content="Proslijedi finansijama"
                variant="secondary"
                size="sm"
                onClick={handleSubmit(sendToFinance)}
                disabled={orders[0]?.passed_to_finance}
              />
            )}
            <Button
              content={
                orders[0]?.is_pro_forma_invoice
                  ? 'Štampaj predračun'
                  : orders[0]?.invoice_number !== ''
                  ? 'Štampaj račun'
                  : 'Štampaj narudžbenicu'
              }
              size="sm"
              variant="secondary"
              onClick={printOrder}
            />
            <Button
              content="Kreiraj prijemnicu"
              size="sm"
              variant="secondary"
              disabled={orders[0]?.status === 'Receive'}
              onClick={handleAddReceiveItems}
            />
          </ButtonContainer>
        </OrderInfo>

        <Table tableHeads={tableHeads} data={mappedOrder || []} isLoading={loading} />

        {orders[0]?.date_system && (
          <AccordionWrapper>
            <Accordion
              style={{border: 0, padding: 0, marginBottom: 20, display: 'block'}}
              isOpen={isOpen === orders[0]?.id ? true : false}
              customHeader={
                <AccordionHeader>
                  <Typography variant="bodyMedium" content={`Prijemnica - ${orders[0].id}`} style={{fontWeight: 600}} />
                  <AccordionIconsWrapper isOpen={isOpen === orders[0]?.id ? true : false}>
                    <ChevronDownIcon
                      width="15px"
                      height="8px"
                      onClick={(e: any) => {
                        e.stopPropagation();
                        openAccordion(orders[0]?.id || 0);
                      }}
                    />
                  </AccordionIconsWrapper>
                </AccordionHeader>
              }
              content={
                <>
                  <ReceiveItemsTable
                    data={orders[0]}
                    fetch={fetch}
                    loading={loading}
                    isDisabled={orders[0]?.passed_to_finance}
                  />
                </>
              }
            />
          </AccordionWrapper>
        )}

        <FormFooter>
          <FormControls>
            <Button
              content="Nazad"
              variant="secondary"
              onClick={() => {
                navigate('/accounting/order-form');
                breadcrumbs.remove();
              }}
            />
          </FormControls>
          {!orders[0]?.invoice_date && !orders[0]?.date_system && (
            <FormControls>
              <Button
                content="Sačuvaj"
                variant="primary"
                onClick={handleSubmit(onSubmit)}
                disabled={uploadedFile === null}
              />
            </FormControls>
          )}
        </FormFooter>

        {showModal && (
          <ReceiveItemsModal open={showModal} onClose={closeModal} fetch={fetch} data={orders} alert={alert} />
        )}

        <NotificationsModal
          open={!!showDeleteModal}
          onClose={handleCloseDeleteModal}
          handleLeftButtomClick={handleDelete}
          subTitle={'Ovaj fajl ce biti trajno izbrisan iz sistema'}
        />
      </SectionBox>
    </ScreenWrapper>
  );
};

import {Button, Datepicker, Dropdown} from 'client-library';
import {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import useAppContext from '../../context/useAppContext';
import useGetMovementArticles from '../../services/graphql/movementArticles/useGetMovementArticles';
import useGetOfficesOfOrganizationUnits from '../../services/graphql/officesOfOrganisationUnit/hooks/useGetOfficeOfOrganizationUnits';
import useGetOrganizationUnits from '../../services/graphql/organizationUnits/hooks/useGetOrganizationUnits';
import useGetOverallSpendingReport from '../../services/graphql/overallSpendingReport/useGetOverallSpendingReport';
import useGetStockOverview from '../../services/graphql/stock/hooks/useGetStockOverview';
import ScreenWrapper from '../../shared/screenWrapper';
import {CustomDivider, MainTitle} from '../../shared/styles';
import {DropdownDataNumber, DropdownDataString} from '../../types/dropdownData';
import {parseDateForBackend} from '../../utils/dateUtils';
import {useDebounce} from '../../utils/useDebounce';
import {AccountingReportType, accountingReportTypeOptions} from './constants';
import {Container, OfficeOptionsRow, Options, OptionsRow} from './styles';

type AccountingReportFilterState = {
  type: DropdownDataString | null;
  start_date: Date | null;
  end_date: Date | null;
  office: DropdownDataNumber | null;
  organization_unit_id: DropdownDataNumber | null;
  exception: boolean | null;
  articles: {value: string; label: string}[] | null;
  date: Date | null;
};

const initialValues: AccountingReportFilterState = {
  type: accountingReportTypeOptions[0],
  start_date: null,
  end_date: null,
  office: null,
  organization_unit_id: null,
  articles: null,
  exception: null,
  date: null,
};

const AccountingReports = () => {
  const [search, setSearch] = useState('');

  const {
    reportService: {generatePdf},
    contextMain,
    alert,
  } = useAppContext();

  const orgUnitId = contextMain?.organization_unit?.id;
  const isSuperAdmin = contextMain?.role_id === 1;

  const {officesOfOrganizationUnits: officeOptions} = useGetOfficesOfOrganizationUnits(0, orgUnitId, '');
  const {organizationUnits: organizationUnitOptions} = useGetOrganizationUnits();

  const {
    handleSubmit,
    control,
    formState: {isValid, errors},
    watch,
    setValue,
  } = useForm({defaultValues: initialValues});

  const {type, start_date, end_date, office, articles, date, organization_unit_id} = watch();

  const {fetch: fetchStock} = useGetStockOverview(undefined, true);

  const {lazyFetch} = useGetOverallSpendingReport();

  const debouncedSearch = useDebounce(search, 500);

  const {articles: movementArticles} = useGetMovementArticles(
    debouncedSearch,
    type?.id !== AccountingReportType.OFFICE_SPENDING,
  );

  const onSubmit = async () => {
    if (type?.id === AccountingReportType.STOCK) {
      if (date) {
        const stockArticles = await fetchStock(parseDateForBackend(date)!);

        if (!stockArticles || stockArticles.length === 0) {
          alert.error('Nema artikala za ovaj datum!');
          return;
        }

        generatePdf('ACCOUNTING_SPENDING', {articles: stockArticles, type: AccountingReportType.STOCK});
      }
    } else {
      if (!isValid) return;

      const orgUnit = isSuperAdmin
        ? organization_unit_id
          ? organization_unit_id.id
          : null
        : contextMain?.organization_unit?.id;

      const data = await lazyFetch({
        start_date: parseDateForBackend(start_date) || null,
        end_date: parseDateForBackend(end_date) || null,
        office_id: office?.id || null,
        articles: articles ? articles.map(opt => opt.value) : null,
        exception: type?.id === AccountingReportType.EXCEPTED_FROM_PLAN ? true : null,
        organization_unit_id: orgUnit,
      });

      if (!data || data.length === 0) {
        alert.error('Nema artikala za ovaj period!');
        return;
      }

      generatePdf('ACCOUNTING_SPENDING', {articles: data, type: type?.id});
    }
  };

  useEffect(() => {
    if (type && type.id === AccountingReportType.EXCEPTED_FROM_PLAN) {
      setValue('exception', true);
    } else {
      setValue('exception', null);
    }

    if (type && type.id !== AccountingReportType.OFFICE_SPENDING) {
      setValue('office', null);
      setValue('articles', null);
    }
  }, [type]);

  const validateDate = () => {
    if (start_date && end_date) {
      if (start_date > end_date) {
        return 'Datum OD ne može biti veći od datuma DO!';
      }
    }
  };

  const articleOptions = useMemo(() => {
    return movementArticles?.map((item: any) => ({id: item, title: item})) as any;
  }, [movementArticles]);

  const onSearch = (value: string) => {
    setSearch(value);
  };

  return (
    <ScreenWrapper>
      <Container>
        <MainTitle content="IZVJEŠTAJI" variant="bodyMedium" />
        <CustomDivider />

        <Options>
          <OptionsRow>
            <Controller
              control={control}
              name="type"
              rules={{required: 'Ovo polje je obavezno!'}}
              render={({field: {onChange, value}}) => (
                <Dropdown
                  label="TIP IZVJEŠTAJA"
                  value={value}
                  onChange={onChange}
                  options={accountingReportTypeOptions}
                  isRequired
                  error={errors.type?.message}
                />
              )}
            />
            {type?.id === AccountingReportType.STOCK ? (
              <Controller
                name="date"
                rules={{required: {value: type?.id === AccountingReportType.STOCK, message: 'Ovo polje je obavezno!'}}}
                control={control}
                render={({field: {onChange, name, value}}) => (
                  <Datepicker
                    onChange={onChange as any}
                    label="DATUM:"
                    name={name}
                    selected={value ? new Date(value) : ''}
                    isRequired
                    error={errors.date?.message}
                  />
                )}
              />
            ) : (
              <>
                <Controller
                  name="start_date"
                  rules={{validate: validateDate}}
                  control={control}
                  render={({field: {onChange, name, value}}) => (
                    <Datepicker
                      onChange={onChange as any}
                      label="DATUM OD:"
                      name={name}
                      selected={value ? new Date(value) : ''}
                      isRequired
                      error={errors.start_date?.message}
                    />
                  )}
                />
                <Controller
                  name="end_date"
                  control={control}
                  rules={{required: 'Ovo polje je obavezno!', validate: validateDate}}
                  render={({field: {onChange, name, value}}) => (
                    <Datepicker
                      onChange={onChange as any}
                      label="DATUM DO:"
                      name={name}
                      selected={value ? new Date(value) : ''}
                      isRequired
                      error={errors.end_date?.message}
                    />
                  )}
                />
              </>
            )}
          </OptionsRow>

          {type?.id === AccountingReportType.OFFICE_SPENDING && (
            <OfficeOptionsRow>
              <Controller
                control={control}
                name="office"
                render={({field: {onChange, value}}) => (
                  <Dropdown
                    label="KANCELARIJA:"
                    value={value}
                    onChange={onChange}
                    options={officeOptions}
                    isRequired
                    error={errors.office?.message}
                  />
                )}
              />
              <Controller
                control={control}
                name="articles"
                render={({field: {onChange, value}}) => (
                  <Dropdown
                    label="ARTIKLI:"
                    value={value as any}
                    onChange={onChange}
                    options={articleOptions as any}
                    isMulti={true}
                    isSearchable={true}
                    // Remove this when component is fixed in devkit and storybook
                    //@ts-ignore
                    onInputChange={onSearch}
                  />
                )}
              />
            </OfficeOptionsRow>
          )}

          {type?.id === AccountingReportType.EXCEPTED_FROM_PLAN && isSuperAdmin && (
            <Controller
              control={control}
              name="organization_unit_id"
              render={({field: {onChange, value}}) => (
                <Dropdown
                  label="ORGANIZACIONA JEDINICA:"
                  value={value}
                  onChange={onChange}
                  options={organizationUnitOptions}
                  isDisabled={!!office}
                  style={{maxWidth: '320px'}}
                />
              )}
            />
          )}
        </Options>

        <Button content="Generiši izvještaj" onClick={handleSubmit(onSubmit)} style={{width: 'fit-content'}} />
      </Container>
    </ScreenWrapper>
  );
};

export default AccountingReports;

import {Tab} from '@oykos-development/devkit-react-ts-styled-components';
import {useEffect, useMemo, useState} from 'react';
import useAppContext from '../../context/useAppContext';
import ScreenWrapper from '../../shared/screenWrapper';
import {Tabs, getCurrentTab, getRouteName, stockTabs} from './constants';
import {MovementList} from './movementList';
import {StockReview} from './stockReview';
import {CustomDivider, MainTitle, SectionBox, StyledTabs, TitleTabsWrapper} from './styles';

export const StockScreen = () => {
  const {
    navigation: {
      navigate,
      location: {pathname},
    },
  } = useAppContext();
  const [activeTab, setActiveTab] = useState(getCurrentTab(location.pathname) || 1);
  const stockPath = pathname && pathname.split('/')[pathname.split('/').length - 1];

  const onTabChange = (tab: Tab) => {
    setActiveTab(tab.id as number);
    const routeName = getRouteName(tab.title as string);

    const pathname = location.pathname.split('/');
    pathname.pop();

    navigate(`${pathname.join('/')}/${routeName}`);
  };

  const stockRoute = useMemo(() => {
    switch (stockPath) {
      case 'movement':
        return <MovementList />;
      default:
        return <StockReview />;
    }
  }, [stockPath]);

  const getTitle = () => {
    switch (activeTab) {
      case Tabs.Stock:
        return 'PREGLED';
      case Tabs.AssetMovement:
        return 'LISTA SVIH OTPREMNICA';

      default:
        throw new Error(`Tab ${activeTab} does not exist`);
    }
  };
  useEffect(() => {
    setActiveTab(getCurrentTab(location.pathname) || 1);
  }, [location.pathname]);

  return (
    <ScreenWrapper>
      <SectionBox>
        <TitleTabsWrapper>
          <MainTitle variant="bodyMedium" content={getTitle()} style={{marginBottom: 0}} />
          <StyledTabs tabs={stockTabs} activeTab={activeTab} onChange={onTabChange} />
        </TitleTabsWrapper>
        <CustomDivider style={{marginTop: 0}} />
        {stockRoute}
      </SectionBox>
    </ScreenWrapper>
  );
};
